import React, { useState, useEffect } from "react";
import "../App.css";
import Table from "./Table";
import Form from "./Form";
import Pop from "./Pop";
import TableItem from "./TableItem";
import Image from "./Image";
import { render } from "@testing-library/react";
import ebird from "../ebird";
import axios from "axios";

function App() {
  //State initialized, will accept data from api response
  const [data, setData] = useState([]);
  const [tableData, setTableData] = useState([]);
  const [lat, setLat] = useState(0.0);
  const [lng, setLng] = useState(0.0);
  const [tableDisplay, setTableDisplay] = useState(false);
  const [pic, setPic] = useState("");

  // success function from geoloc
  navigator.geolocation.getCurrentPosition(success);
  function success(pos) {
    setLat(pos.coords.latitude.toFixed(2));
    setLng(pos.coords.longitude.toFixed(2));
    console.log(lat, lng);
  }

  // function success(pos) {
  //   console.log("succalled");
  //   const crd = pos.coords;
  //   console.log(crd);
  //   setLat(pos.coords.latitude.toFixed(2));
  //   setLng(pos.coords.longitude.toFixed(2));
  //   console.log(ebird_url);
  // }

