  //   Accept: "application/json, text/plain",
  //   "Content-type": "application/json",
  //   "x-ebirdapitoken": "47fqctovm0gd",
  // };

  const ebird_url = `http://localhost:5000/pop/${lat}/${lng}`;

  function onClickProp(e) {
    setTableDisplay(true);
    if (lat === 0) {
      alert('please allow loc')
    }
      async function fetchData() {
        let res = await axios(ebird_url);
        console.log(res);
        setData(res.data);
        setTableData(data.slice(0, 20));
      }
      fetchData();
    
  }

  const onClickPic = function onClickPic(code) {
    const ebird_redirect = `https://ebird.org/species/${code}`;
    window.open(ebird_redirect);
  };

  return (
    <div className="App">
      <header className="header-container">
        <h1 className="ui header">Bird Tracker</h1>
      </header>
      <div id="main-container">
        <div id="form-container" className="ui container">
          <Pop className="form-item" onClickProp={onClickProp} />
          <Form data={data} className="form-item" />
        </div>
        <div className="ui container">
          <Table onClickPic={onClickPic} tableData={tableData} />
        </div>
      </div>
    </div>
  );
}

export default App;
