import React, { useMemo } from "react";
import { useTable, useSortBy } from "react-table";
import { COLUMNS } from "./columns";

export default function BasicTable({ initData, tableDisplay }) {
  const columns = useMemo(() => COLUMNS, []);
  const data = useMemo(() => initData, [initData]);
  console.log(initData);

  const tableInstance = useTable(
    {
      columns,
      data,
    },
    useSortBy
  );

  const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow } =
    tableInstance;

  return (
    <div>
      <table className="ui blue table" {...getTableProps()}>
        <thead>
          {headerGroups.map((headerGroup) => (
            <tr {...headerGroup.getHeaderGroupProps()}>
              {headerGroup.headers.map((column) => (
                <th {...column.getHeaderProps()}>
                  {column.render("Header")}
                  <span>
                    {column.isSorted ? (column.isSorted ? "ðŸ”¼" : "ðŸ”½") : ""}
                  </span>
                </th>
              ))}
            </tr>
          ))}
        </thead>
        <tbody {...getTableBodyProps()}>
          {rows.map((row) => {
            prepareRow(row);
            return (
              <tr
                {...row.getRowProps({
                  onClick: () => {
                    console.log(props.cell.row.original)
                    // const ebird_redirect = `https://ebird.org/species/${props.cell.row.original["speciesCode"]}`;
                    // window.open(ebird_redirect);
                    // console.log(props.cell.row.original);
                  },
                })}
              >
                {row.cells.map((cell) => {
                  console.log(cell.column);
                  return (
                    <td {...cell.getCellProps()}>
                      {cell.render(
                        "Cell"
                        // { com: "com" },
                        // { sci: "sci" }
                      )}
                    </td>
                  );
                })}
              </tr>
            );
          })}
          <tr>
            <td></td>
          </tr>
        </tbody>
      </table>
    </div>
  );
}
