import React, { useState, useEffect } from "react";
import "../App.css";
import Table from "./Table";
import Form from "./Form";
import Pop from "./Pop";
import TableItem from "./TableItem";
import Image from "./Image";
import { render } from "@testing-library/react";
import ebird from "../ebird";
import axios from "axios";

function App() {
  //State initialized, will accept data from api response
  const [data, setData] = useState([]);
  const [lat, setLat] = useState(0.0);
  const [lng, setLng] = useState(0.0);
  const [tableDisplay, setTableDisplay] = useState(false);
  const [pic, setPic] = useState("");

  //success function from geoloc
  // navigator.geolocation.getCurrentPosition(success);
  // function success(pos) {
  //   setLat(pos.coords.latitude.toFixed(2));
  //   setLng(pos.coords.longitude.toFixed(2));
  //   console.log(lat, lng);
  // }

  useEffect(() => {
    function success(pos) {
      setLat(pos.coords.latitude.toFixed(2));
      setLng(pos.coords.longitude.toFixed(2));
      console.log(lat, lng);
    }
    function getLocation() {
      navigator.geolocation.getCurrentPosition(success);
    }
    getLocation();
  }, [data]);
  //get data from API; stored in variable for call in useEffect
  axios.defaults.headers.common = {
    mode: "cors",
    Accept: "application/json, text/plain",
    "Content-type": "application/json",
    "x-ebirdapitoken": "47fqctovm0gd",
  };
  //use imported api module instead of writing API call in App.jsx

  // useEffect(() => {
  //   async function fetchData() {
  //     let res = await axios(
  //       "http://localhost:5000/pop"
  //     );
  //     console.log(res)
  //     // setData(res.data);
  //   }
  //   fetchData();
  // }, [tableDisplay]);
  const ebird_url = `http://localhost:5000/pop/${lat}/${lng}`;
  function onClickProp(e) {
    //Iterate over data to generate rows/data cells
    setTableDisplay(true);
    console.log("click");
    console.log(data);
    console.log(tableDisplay);
    async function fetchData() {
      let res = await axios(ebird_url);
      console.log(res.data);
      setData(res.data);
    }
    fetchData();

    //console.log(data)
    //console.log("oCPfunc");
  }

  return (
    <div className="App">
      <header className="header-container">
        <h1 className="ui header">Bird Tracker</h1>
      </header>
      <div id="main-container">
        <div className="ui container">
          <Pop className="pop" onClickProp={onClickProp} />
          <Form />
        </div>

        <div className="ui container">
          <Table tableData={data} />
        </div>
        <div className="ui container"><Image /></div>

      </div>
    </div>
  );
}

export default App;
