import React, { useState, useEffect } from "react";
import "../App.css";
import Table from "./Table";
import Form from "./Form";
import Pop from "./Pop";
import TableItem from "./TableItem";
import Image from "./Image";
import { render } from "@testing-library/react";
import ebird from "../ebird";
import axios from "axios";

function App() {
  //State initialized, will accept data from api response
  const [data, setData] = useState([]);
  const [tableData, setTableData] = useState([]);
  const [lat, setLat] = useState(0.0);
  const [lng, setLng] = useState(0.0);
  const [tableDisplay, setTableDisplay] = useState(false);
  const [pic, setPic] = useState("");

  // // success function from geoloc
  // navigator.geolocation.getCurrentPosition(success);
  // function success(pos) {
  //   setLat(pos.coords.latitude.toFixed(2));
  //   setLng(pos.coords.longitude.toFixed(2));
  //   console.log(lat, lng);
  // }
 
//  function success(pos) {
  
//    console.log('succalled')
//    const crd = pos.coords;
//    console.log(crd)
//     setLat(pos.coords.latitude.toFixed(2));
//     setLng(pos.coords.longitude.toFixed(2));
//     console.log(ebird_url);
//   }
  
 
//     // function error(err){
//     //   console.warn('error bitch')
//     // }
//    navigator.geolocation.getCurrentPosition((position)=>{
     
//     success(position)
//     console.log(lat)
//    })

var options = {
  enableHighAccuracy: true,
  timeout: 5000,
  maximumAge: 0
};

function success(pos) {
  var crd = pos.coords;

  console.log('Your current position is:');
  console.log(`Latitude : ${crd.latitude}`);
  console.log(`Longitude: ${crd.longitude}`);
  console.log(`More or less ${crd.accuracy} meters.`);
}

function error(err) {
  console.warn(`ERROR(${err.code}): ${err.message}`);
}

navigator.geolocation.getCurrentPosition(success, error, options);
 

  
    
   

  // axios.defaults.headers.common = {
  //   mode: "cors",
  //   Accept: "application/json, text/plain",
  //   "Content-type": "application/json",
  //   "x-ebirdapitoken": "47fqctovm0gd",
  // };

  const ebird_url = `http://localhost:5000/pop/${lat}/${lng}`;

  function onClickProp(e) {
    setTableDisplay(true);
    async function fetchData() {
      let res = await axios(ebird_url);
      console.log(res);
      setData(res.data);
      setTableData(data.slice(0, 20));
     
    }
    fetchData();
    
  }



  const onClickPic = function onClickPic(code) {
    const ebird_redirect = `https://ebird.org/species/${code}`;
    window.open(ebird_redirect);
  };



  return (
    <div className="App">
      <header className="header-container">
        <h1 className="ui header">Bird Tracker</h1>
      </header>
      <div id="main-container">
        <div id="form-container" className="ui container">
          <Pop className="form-item" onClickProp={onClickProp} />
          <Form data={data} className="form-item" />
        </div>
        <div className="ui container">
          <Table onClickPic={onClickPic} tableData={tableData} />
        </div>
      </div>
    </div>
  );
}

export default App;
