import React, { useState, useEffect } from "react";
import "../App.css";
import Table from "./Table";
import Form from "./Form";
import Pop from "./Pop";
import TableItem from "./TableItem";
import Image from "./Image";
import { render } from "@testing-library/react";
import ebird from "../ebird";
import axios from "axios";

function App() {
  //State initialized, will accept data from api response
  const [data, setData] = useState([]);
  const [tableData, setTableData] = useState([]);
  const [lat, setLat] = useState(0.0);
  const [lng, setLng] = useState(0.0);
  const [tableDisplay, setTableDisplay] = useState(false);
  const [pic, setPic] = useState("");
  const [renderTable, setRenderTable] = useState([]);

  // success function from geoloc
  navigator.geolocation.getCurrentPosition((pos) => {
    setLat(pos.coords.latitude.toFixed(2));
    setLng(pos.coords.longitude.toFixed(2));
    console.log(lat, lng);
  });

  // axios.defaults.headers.common = {
  //   mode: "cors",
  //   Accept: "application/json, text/plain",
  //   "Content-type": "application/json",
  //   "x-ebirdapitoken": "47fqctovm0gd",
  // };
  // function row() {
  //   console.log("rowcalled");
  //   if (tableDisplay === true) {
  //     return tableData.map((el) => (
  //       <tr onClick={(e) => onClickPic(el.speciesCode)} className={el.comName}>
  //         <td colSpan="1">{el.comName}</td>
  //         <td colSpan="1">{el.sciName}</td>
  //         <td>{el.locName}</td>
  //         <td>{el.obsDt}</td>
  //       </tr>
  //     ));
  //   }

  const ebird_url = `http://localhost:5000/pop/${lat}/${lng}`;

  const render = setRenderTable(
    tableData.map((el) => (
      <tr onClick={(e) => onClickPic(el.speciesCode)} className={el.comName}>
        <td colSpan="1">{el.comName}</td>
        <td colSpan="1">{el.sciName}</td>
        <td>{el.locName}</td>
        <td>{el.obsDt}</td>
      </tr>
    ))
  );

  function onClickProp(e) {
    setTableDisplay(true);
    console.log("clickprop");
    async function fetchData() {
      let res = await axios(ebird_url);
      console.log(res);
      setData(res.data);
      setTableData(data.slice(0, 20));
    }
    fetchData();
    
  }

  const onClickPic = function onClickPic(code) {
    const ebird_redirect = `https://ebird.org/species/${code}`;
    window.open(ebird_redirect);
  };

  return (
    <div className="App">
      <header className="header-container">
        <h1 className="ui header">Bird Tracker</h1>
      </header>
      <div id="main-container">
        <div id="form-container" className="ui container">
          <Pop
            className="form-item"
            lng={lng}
            lat={lat}
            onClickProp={onClickProp}
          />
          <Form data={data} className="form-item" />
        </div>
        <div className="ui container">
          <Table
            renderTable={renderTable}
            onClickPic={onClickPic}
            tableData={tableData}
            tableDisplay={tableDisplay}
          />
        </div>
      </div>
    </div>
  );
}

export default App;
